def get_random_string(length):
    """
    Generates a random string of specified length using lowercase letters.

    Parameters:
    length (int): The desired length of the random string.

    Returns:
    str: A random string of the given length.
    """
    if not isinstance(length, int) or length < 5 or length > 15:
        raise ValueError("Length must be an integer between 5 and 15.")
    return ''.join(random.choices(string.ascii_lowercase, k=length))

